css:
.print-format div, .print-format span, .print-format td, .print-format h1, .print-format h2, .print-format h3, .print-format h4 {
    font-family: 'Lato', sans-serif
}

.text-right {
    text-align: right;
    font-weight: bold;
}

.row {
    margin-right: 0;
    margin-left: 0;
}

.col-md-6 {
  padding-left: 0;
  padding-right: 0;
  display: inline-block;
  width: 49%;
  margin-bottom: 6px;
  vertical-align: middle;
  color: #000;
}

.billing_shipping_info {
    margin-top: 30px;
}

table {
    width: 100%;
    margin-top: 30px;
}

h2 {
    font-size: 22px;
    font-weight: 800;
    text-transform: uppercase;
}

.col-md-6 h3 {
    color: #000;
    text-transform: uppercase;
    font-size: 14px;
    margin-top: 0
}

.bill_to {
    margin-right: 20px;
}

.col-md-6 .row .col-md-6 p {
    font-size: 14px;
    font-weight: 600;
    margin: 0;
}

.col-md-6 .row .col-md-6 h3 {
    margin-bottom: 4px;
}

table thead tr {
    color: #000;
    text-transform: uppercase;
    padding: 5px 13px;
    font-size: 14px;
}

table tr {
    border: 1px solid gray;
}

table td,
table th {
    border-right: 1px solid gray;
}

table thead tr th:first-child {
    padding-left: 13px!important
}
